import input from 'readline-sync';

console.log("Aplicação de juros")

var sair = ''

while (sair !== 'sair') {

    let valor = input.question("Informe o valor devido: R$ ")
    valor = parseInt(valor)

    if (valor > 0) {

        let dias = input.question("Informe quantos dias se passaram desde o vencimento: ")

        if (dias > 0) {

            if (dias > 15) {
                let taxa = 0.1
                let juros = Number(valor) + (valor * taxa);
                console.log("Valor origínal da dívida: R$ " + valor)
                console.log("Dias atrasados:  " + dias)
                console.log("Taxa de juros: 10%")
                console.log("Valor total com juros: R$ " + juros)
            }
            else {
                let taxa = 0.05
                let juros = Number(valor) + (valor * taxa);
                console.log("Valor origínal da dívida: R$ " + Number(valor))
                console.log("Dias atrasados:  " + dias)
                console.log("Taxa de juros: 5%")
                console.log("Valor total com juros: R$ " + juros)
            }
        }
        else {
            console.log("Você esta em dia!")
        }
    }
    else {

        console.log("O valor da dívida deve ser maior que zero!")
    }

    var sair = input.question("Para sair digite 'sair', ou pressione Enter para continuar: ");
}
